package set1

import base64ToHex
import hammingDistance
import io.kotlintest.shouldBe
import io.kotlintest.specs.StringSpec

class Challenge6BreakRepeatingKeyXorTest : StringSpec({

    "hamming 'this is a test' 'wokka wokka!!!'" {
        val one = "this is a test"
        val two = "wokka wokka!!!"

        one.toByteArray().hammingDistance(two.toByteArray()) shouldBe 37
    }

    "base64 to hex" {
        val base64 = ""

        val expected = ""
        base64ToHex(base64) shouldBe expected
    }

    "transposed by 4" {
        val hex = ""

        val expected = listOf(
            "",
            "",
            "",
            ""
        )

        hex.transposed(4) shouldBe expected
    }

    "transposed by 5" {
        val hex = ""

        val expected = listOf(
            "",
            "",
            "",
            "",
            ""
        )

        hex.transposed(5) shouldBe expected
    }

    "transposed by 6" {
        val hex = ""

        val expected = listOf("",
            "",
            "",
            "",
            "",
            ""
        )

        hex.transposed(6) shouldBe expected
    }

    "given example" {
        val file = "/challenge-6-string.txt"

        breakRepeatingKeyXor(file) shouldBe ""
    }

})
